import React from 'react';
//import ReactDOM from 'react-dom';
import axios from 'axios';




class LoginForm extends React.Component{
   constructor(props){
        super(props);
        this.state={formFields:{
            username:"",
            password:"",
        }      
        };

    }

    submitLogin(formFields){  
        console.log("inside"+formFields.username);
        axios.post('/test', formFields)
            .then(function(response){
                console.log(response);

        })
     .catch(function(error){
       console.log(error);
       //Perform action based on error
     });
  }



    /*componentWillMount() {
        this.callAPI();
    }*/

    /*updateInput(e){
        let statusCopy = Object.assign({}, this.state);
        let inputtype=e.target.name;
        statusCopy.formFields[inputtype] = e.target.value;
        statusCopy.formFields[inputtype] = e.target.value;
        this.setState(statusCopy);
    }*/

    render(){
        return(

            <div className="outer-wrapper">  
                <div className="outer-box">
                    <div className="header">
                        Sign In
                    </div>
                    <div className="inner-box">
                        <form onSubmit={this.submitLogin(this.state.formFields)}>
                            <div  className="form-group">
                                <label>User Name  </label>
                                <input id="userinput" name="username" className="input-group-text" type="text" onChange={e => this.setState({formFields:{username:e.target.value}})}/>   
                            </div>

                            <div  className="form-group">
                                <label>Password  </label>
                                <input id="password" name="password" className="input-group-text" type="password" onChange={e => this.setState({formFields:{password:e.target.value}})}/>

                            </div>

                            <input id="submit-button" type="submit" value="Sign In" className="btn btn-info" />
                        </form>
                    </div>
                </div>                  
            </div>

        );
    }

}
export default LoginForm;
