import axios from 'axios';
export const LOGGEDIN_USER = 'users:loggedInUser';

export default function logInUser(loggedUser) {
    return (dispatch) => axios.post('/user/login', loggedUser)
        .then(user => {
            console.log("in sucess");
            dispatch({
                type: LOGGEDIN_USER,
                payload: {user:user}
            });

            return user;
        })
}
