import React from 'react';
//import ReactDOM from 'react-dom';
import axios from 'axios';




class LoginForm extends React.Component{
   constructor(props){
        super(props);
        this.state={formFields:{
            username:'',
            password:'',
        }      
        };
        this.updateUsername = this.updateUsername.bind(this)
        this.updatePassword = this.updatePassword.bind(this)
    }

    submitLogin(formFields){  
        console.log(this.state.formFields.username);
        axios.post('/test', formFields)
            .then(function(response){
                console.log(response);

        })
     .catch(function(error){
       console.log(error);
       //Perform action based on error
     });
  }



    /*componentWillMount() {
        this.callAPI();
    }*/

    updateInput(e){
        let statusCopy = Object.assign({}, this.state.formFields);
        statusCopy.formFields[e.target.name].value = e.target.value;
        this.setState(statusCopy);
    }

    render(){
        return(

            <div className="outer-wrapper">  
                <div className="outer-box">
                    <div className="header">
                        Sign In
                    </div>
                    <div className="inner-box">
                        <form onSubmit={this.submitLogin(this.state.formFields)}>
                            <div  className="form-group">
                                <label>User Name  </label>
                                <input id="userinput" className="input-group-text" type="text" onChange={this.updateInput}/>   
                            </div>

                            <div  className="form-group">
                                <label>Password  </label>
                                <input id="password" className="input-group-text" type="password" onChange={this.updateInput}/>

                            </div>

                            <input id="submit-button" type="submit" value="Sign In" className="btn btn-info" />
                        </form>
                    </div>
                </div>                  
            </div>

        );
    }

}
export default LoginForm;
