import React from 'react';
//import ReactDOM from 'react-dom';




class LoginForm extends React.Component{
   /* constructor(props){
        super(props);
        this.state={
            user_name:'',
            password:'',
        };
    }

    submitLogin(event){
        //event.preventDefault();

    }*/

    constructor(props) {
        super(props);
        this.state = { 
            username: "",
            password:""
        };
    }

    callAPI() {
        fetch("http://localhost:5000/test")
            .then(response => response.json())
            .then(response => this.setState({apiResponse :response}));

    }

    componentWillMount() {
        this.callAPI();
    }

    render(){
        console.log(this.state);
        return(
            /*<div>
                <p>{this.state.apiResponse.name}</p>
            </div>*/

            <div class="outer-wrapper">  
                <div class="outer-box">
                    <div class="header">
                        Sign In
                    </div>
                    <div class="inner-box">
                        <form onSubmit="this.submitLogin">
                            <div  className="form-group">
                                <label>User Name  </label>
                                <input id="userinput" className="input-group-text" type="text"/>   
                            </div>

                            <div  className="form-group">
                                <label>Password  </label>
                                <input id="password" className="input-group-text" type="password" onChange={e=>this.setState}/>

                            </div>

                            <input id="submit-button" type="submit" value="Sign In" className="btn btn-info"/>
                        </form>
                    </div>
                </div>                  
            </div>

        );
    }

}
export default LoginForm;
