import React from 'react';
//import ReactDOM from 'react-dom';
import axios from 'axios';




class LoginForm extends React.Component{
   constructor(props){
        super(props);
        this.state={formFields:{
            username:"",
            password:"",
        }      
        };

    }

    handleSubmit(e){  
        //console.log(this.state.fusername);
        //console.log(this.state.password);
        let data={
            username:this.state.formFields["username"],
            password:this.state.formFields["password"]
        }
        axios.post('/test', data)
            .then(function(response){
                console.log(response);

        })
     .catch(function(error){
       console.log(error);
       //Perform action based on error
     });
  }



    /*componentWillMount() {
        this.callAPI();
    }*/

    updateUsername(e){
        this.setState({formFields:{username:e.target.value}})
    }

    updatePassword(e){
        this.setState({formFields:{password:e.target.value}})
    }

    render(){
        return(

            <div className="outer-wrapper">  
                <div className="outer-box">
                    <div className="header">
                        Sign In
                    </div>
                    <div className="inner-box">
                        <form>
                            <div  className="form-group">
                                <label>User Name  </label>
                                <input id="userinput" name="username" className="input-group-text" type="text" value={this.state.formFields["username"]} onChange={this.updateUsername.bind(this)}/>   
                            </div>

                            <div  className="form-group">
                                <label>Password  </label>
                                <input id="password" name="password" className="input-group-text" type="password" value={this.state.formFields["password"]} onChange={this.updatePassword.bind(this)}/>

                            </div>

                            <input id="submit-button" type="submit" value="Sign In" className="btn btn-info" onClick={this.handleSubmit}/>
                        </form>
                    </div>
                </div>                  
            </div>

        );
    }

}
export default LoginForm;
